import { createActions } from "@reduxjs/toolkit";
import { v4 as uuidv4 } from "uuid";

export const addContact = createActions("contact/add", (name, number) => ({
  payload: {
    id: uuidv4(),
    name,
    number,
  },
}));
export const deleteContact = createActions("contact/delete");
export const filterContact = createActions("contact/filter");
